# To create a baseline characteristics table, we need a dataset with one
# row per participant. We can create this from our final dataset by taking
# the first row for each participant.
retreat_baseline &lt;- retreat_final |&gt;
  arrange(idr, desc(change_contr_tdc)) |&gt;
  distinct(idr, .keep_all = TRUE)

# Vector of covarites of interest for Table 1:
covariates_of_interest &lt;- c("age", "childfuture", "live2y", "alcprob", "educgp", "margp")
num_vars&lt;-names(
  retreat_baseline|&gt;
    select(covariates_of_interest) |&gt;
    select_if(is.numeric)
  )

non_num_vars&lt;-names(
  retreat_baseline|&gt;
    select(covariates_of_interest) |&gt;
    select(-any_of(c(num_vars)))
)

# Use the gtsummary package to create a publication-quality "Table 1"
# This table will summarize the baseline characteristics of the cohort.
TABLE &lt;- 
  tbl_merge(
    list(

      TABLE_1&lt;-retreat_baseline |&gt;
      select(any_of(covariates_of_interest)) |&gt;
      tbl_summary(
      ) |&gt;
      bold_labels() |&gt; 
      italicize_levels(),

      retreat_baseline |&gt;
      select(change_contr_tdc, any_of(covariates_of_interest)) |&gt;
      mutate(change_contr_tdc = factor(change_contr_tdc, levels = c(0, 1), labels = c("No Change", "Change"))) |&gt;
      tbl_summary(
        by= change_contr_tdc  # Group by pregnancy event
      ) |&gt;
      bold_labels() |&gt; 
      italicize_levels(),

      retreat_baseline |&gt;
        select(event, any_of(covariates_of_interest)) |&gt;
        mutate(event = factor(event, levels = c(0, 1), labels = c("No Pregnancy", "Pregnancy"))) |&gt;
        tbl_summary(
          by= event  # Group by pregnancy event
        ) |&gt;
        bold_labels() |&gt; 
        italicize_levels()
      ), 
    tab_spanner = c("**Cohort**","**Contraception Follow-up**", "**Pregnancy Follow-up Status**")
    ) |&gt;
  modify_caption(
    caption = "Baseline Characteristics of the RETREAT Cohort"
    ) |&gt; 
  modify_header(all_stat_cols() ~ "**{level}**  \nN = {n} ({style_percent(p)}%)")

tbl_merge(list(TABLE_1),tab_spanner = c("**Study Cohort**"))












