func_store_gt_table &lt;- function(
    tbl = .,
    title_num = tbl_TTLNUM,
    title_name = tbl_TTLNAM,
    subtitle_num = tbl_SUBTTLNUM,
    subtitle_name = tbl_SUBTTLNAM,
    levels_total = TRUE
  ) {
  # build labels
  title_lbl    &lt;- paste0(title_num,    ": ", title_name)
  subtitle_lbl &lt;- paste0(subtitle_num, ": ", subtitle_name)

  # wrap in gt and add header
  gt_tbl &lt;- tbl |&gt; 
    modify_header(label = paste0("**",ATTRIBUTES,"**"))
  if(levels_total == TRUE) {
    gt_tbl &lt;- gt_tbl  |&gt; 
      modify_header(all_stat_cols() ~ "**{level}** &lt;br&gt;N = {n} ({style_percent(p)}%)") |&gt;
    as_gt() |&gt;
    tab_header(
      title    = title_lbl,
      subtitle = subtitle_lbl
    )
  } else {
    gt_tbl &lt;- gt_tbl  |&gt; 
      modify_header(all_stat_cols() ~ "**{level}**")  |&gt; 
      as_gt() |&gt;
      tab_header(
        title    = title_lbl,
        subtitle = subtitle_lbl
      )
  }
  # ensure global exists
  if (!exists("ls_tbls", envir = .GlobalEnv) || is.null(ls_tbls)) {
    ls_tbls &lt;&lt;- list()
  }

  # create section if missing
  if (!title_lbl %in% names(ls_tbls)) {
    ls_tbls[[title_lbl]] &lt;&lt;- list()
  }

  # store under subtitle
  ls_tbls[[title_lbl]][[subtitle_lbl]] &lt;&lt;- gt_tbl

  # return the gt object
  return(gt_tbl)
}
